// Inisialisasi DataTable untuk pengguna
let pengguna = $("#pengguna").DataTable({
    responsive: true,
    scrollX: true,
    ajax: readUrl, // URL untuk membaca data
    columnDefs: [
        {
            searchable: false,
            orderable: false,
            targets: 0 // Kolom pertama tidak dapat diurutkan dan dicari
        }
    ],
    order: [[1, "asc"]], // Mengurutkan berdasarkan kolom kedua (username) secara ascending
    columns: [
        { data: null }, // Kolom untuk nomor urut
        { data: "username" }, // Kolom untuk username
        { data: "nama" }, // Kolom untuk nama
        { data: "action" } // Kolom untuk aksi (misalnya, tombol edit)
    ]
});

// Fungsi untuk me-reload tabel
function reloadTable() {
    pengguna.ajax.reload();
}

// Fungsi untuk menambahkan data pengguna baru
function addData() {
    $.ajax({
        url: addUrl, // URL untuk menambahkan data
        type: "post",
        dataType: "json",
        data: $("#form").serialize(),
        success: () => {
            $(".modal").modal("hide");
            Swal.fire("Sukses", "Sukses Menambahkan Data", "success");
            reloadTable();
        },
        error: error => {
            console.log(error);
        }
    });
}

// Fungsi untuk menghapus data pengguna
function remove(a) {
    Swal.fire({
        title: "Hapus",
        text: "Hapus data ini?",
        type: "warning",
        showCancelButton: true
    }).then(result => {
        if (result.value) {
            $.ajax({
                url: deleteUrl, // URL untuk menghapus data
                type: "post",
                dataType: "json",
                data: { id: a },
                success: () => {
                    Swal.fire("Sukses", "Sukses Menghapus Data", "success");
                    reloadTable();
                },
                error: error => {
                    console.log(error);
                }
            });
        }
    });
}

// Fungsi untuk mengedit data pengguna
function editData() {
    $.ajax({
        url: editUrl, // URL untuk mengedit data
        type: "post",
        dataType: "json",
        data: $("#form").serialize(),
        success: () => {
            $(".modal").modal("hide");
            Swal.fire("Sukses", "Sukses Mengedit Data", "success");
            reloadTable();
        },
        error: error => {
            console.log(error);
        }
    });
}

// Fungsi untuk menampilkan form tambah data pengguna
function add() {
    url = "add";
    $(".modal-title").html("Add Data");
    $('.modal button[type="submit"]').html("Add");
}

// Fungsi untuk menampilkan form edit data pengguna
function edit(a) {
    $.ajax({
        url: getPenggunaUrl, // URL untuk mendapatkan data pengguna berdasarkan ID
        type: "post",
        dataType: "json",
        data: { id: a },
        success: data => {
            $('[name="id"]').val(data.id);
            $('[name="username"]').val(data.username);
            $('[name="nama"]').val(data.nama);
            $(".modal").modal("show");
            $(".modal-title").html("Edit Data");
            $('.modal button[type="submit"]').html("Edit");
            url = "edit";
        },
        error: error => {
            console.log(error);
        }
    });
}

// Mengatur nomor urut tabel setelah order atau search
pengguna.on("order.dt search.dt", () => {
    pengguna.column(0, { search: "applied", order: "applied" }).nodes().each((cell, i) => {
        cell.innerHTML = i + 1;
    });
});

// Validasi form menggunakan jQuery Validation Plugin
$("#form").validate({
    errorElement: "span",
    errorPlacement: (error, element) => {
        error.addClass("invalid-feedback");
        element.closest(".form-group").append(error);
    },
    submitHandler: () => {
        // Jika url adalah 'edit', panggil fungsi editData(), jika tidak, panggil addData()
        (url === "edit") ? editData() : addData();
    }
});

// Reset form dan validasi saat modal ditutup
$(".modal").on("hidden.bs.modal", () => {
    $("#form")[0].reset();
    $("#form").validate().resetForm();
});
