// Inisialisasi DataTable untuk satuan produk
let url, satuan_produk = $("#satuan_produk").DataTable({
    responsive: true,
    scrollX: true,
    ajax: readUrl, // URL untuk membaca data
    columnDefs: [
        {
            searchable: false, // Salah ejaan telah diperbaiki
            orderable: false,
            targets: 0 // Kolom pertama tidak dapat diurutkan dan dicari
        }
    ],
    order: [[1, "asc"]], // Mengurutkan berdasarkan kolom kedua (index 1)
    columns: [
        { data: null }, // Kolom untuk nomor urut
        { data: "satuan" }, // Kolom untuk satuan
        { data: "action" } // Kolom untuk aksi
    ]
});

// Fungsi untuk me-reload tabel
function reloadTable() {
    satuan_produk.ajax.reload();
}

// Fungsi untuk menambah data
function addData() {
    $.ajax({
        url: addUrl, // URL untuk menambah data
        type: "post",
        dataType: "json",
        data: $("#form").serialize(), // Mengirimkan data form
        success: () => {
            $(".modal").modal("hide");
            Swal.fire("Sukses", "Sukses Menambahkan Data", "success");
            reloadTable();
        },
        error: a => {
            console.log(a);
        }
    });
}

// Fungsi untuk menghapus data
function remove(a) {
    Swal.fire({
        title: "Hapus",
        text: "Hapus data ini?",
        type: "warning",
        showCancelButton: true
    }).then(e => {
        if (e.value) {
            $.ajax({
                url: removeUrl, // URL untuk menghapus data
                type: "post",
                dataType: "json",
                data: { id: a },
                success: () => {
                    Swal.fire("Sukses", "Sukses Menghapus Data", "success");
                    reloadTable();
                },
                error: a => {
                    console.log(a);
                }
            });
        }
    });
}

// Fungsi untuk mengedit data
function editData() {
    $.ajax({
        url: editUrl, // URL untuk mengedit data
        type: "post",
        dataType: "json",
        data: $("#form").serialize(), // Mengirimkan data form
        success: () => {
            $(".modal").modal("hide");
            Swal.fire("Sukses", "Sukses Mengedit Data", "success");
            reloadTable();
        },
        error: a => {
            console.log(a);
        }
    });
}

// Fungsi untuk menampilkan modal tambah data
function add() {
    url = "add";
    $(".modal-title").html("Add Data");
    $('.modal button[type="submit"]').html("Add");
}

// Fungsi untuk menampilkan modal edit data
function edit(a) {
    $.ajax({
        url: get_satuanUrl, // URL untuk mendapatkan data satuan produk
        type: "post",
        dataType: "json",
        data: { id: a },
        success: a => {
            $('[name="id"]').val(a.id);
            $('[name="satuan"]').val(a.satuan);
            $(".modal").modal("show");
            $(".modal-title").html("Edit Data");
            $('.modal button[type="submit"]').html("Edit");
            url = "edit";
        },
        error: a => {
            console.log(a);
        }
    });
}

// Mengatur nomor urut tabel setelah order atau search
satuan_produk.on("order.dt search.dt", () => {
    satuan_produk.column(0, { search: "applied", order: "applied" }).nodes().each((a, e) => {
        a.innerHTML = e + 1;
    });
});

// Validasi form dan pengaturan submit handler
$("#form").validate({
    errorElement: "span",
    errorPlacement: (a, e) => {
        a.addClass("invalid-feedback");
        e.closest(".form-group").append(a);
    },
    submitHandler: () => {
        if (url === "edit") {
            editData();
        } else {
            addData();
        }
    }
});

// Reset form dan validasi saat modal ditutup
$(".modal").on("hidden.bs.modal", () => {
    $("#form")[0].reset();
    $("#form").validate().resetForm();
});
